import React, { useRef } from "react";
import { Link } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import NAvbsr from "./NAvbsr";
import fullstackdeveloper from "./images/Full-Stack-Developer.png";
import MERN from "./images/MERN Stack Developer.png";

function Certification() {
  const pdfRef = useRef();

  const handleDownloadPDF = async () => {
    const input = pdfRef.current;

    const canvas = await html2canvas(input, { scale: 2 });
    const imgData = canvas.toDataURL("image/png");

    const pdf = new jsPDF("p", "mm", "a4");
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

    pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, pdfHeight);
    pdf.save("certificates.pdf");
  };

  return (
    <>
      <NAvbsr />
      <div className="container mt-3">
        <button className="btn btn-primary mb-3" onClick={handleDownloadPDF}>
          Download Certificates as PDF
        </button>

        <div className="row" ref={pdfRef}>
          <div className="col-lg-6 mb-4">
            <h1 className="mt-3">E-commerse Web App</h1>
            <Link
              to="https://success.simplilearn.com/75f3c9db-25b9-4f13-b035-39ca24ed7037#acc.5ZIwpGmx"
              target="_blank"
              rel="noopener noreferrer"
            >
              <img src={MERN} className="certificate_image img-fluid" alt="MERN Certificate" />
            </Link>
          </div>

          <div className="col-lg-6 mb-4">
            <h1 className="mt-3">Family Hotel Web App</h1>
            <img
              src={fullstackdeveloper}
              className="certificate_image img-fluid"
              alt="Full Stack Certificate"
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default Certification;

